mapper.select:
select d.*, c.name as categoryName from sky_take_out.dish d left join sky_take_out.category c on d.category_id = c.id
and d.name like concat('%',#{name},'%') and d.status = #{status} and d.category_id = #{categoryId}
order by d.update_time desc

mapper.update:
update sky_take_out.dish
description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_Time = #{updateTime}, update_User = #{updateUser}, category_id = #{categoryId},
where id = #{id}

mapper.select:
select * from sky_take_out.dish

    and name like concat('%',#{name},'%')
    and status = #{status}
    and category_id = #{categoryId}

order by create_time desc

select count(id) from dish

     and status = #{status} 
     and category_id = #{categoryId}
