mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select *
from sky_take_out.orders

    and status = #{status}
    and user_id = #{userId}
    and number = #{number}
    and phone = #{phone}
    and order_time between #{beginTime} and #{endTime}


order by order_time desc

select count(id)
from sky_take_out.orders

    and status = 5
    and delivery_time >= #{begin}
    and delivery_time <= #{end}
