mapper.select:
select s.*, c.name as categoryName from sky_take_out.setmeal s left join sky_take_out.category c on s.category_id = c.id
and s.name like concat('%',#{name},'%') and s.status = #{status} and s.category_id = #{categoryId}
order by s.create_time desc

mapper.update:
update sky_take_out.setmeal
description = #{description}, category_id = #{categoryId}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_Time = #{updateTime}, update_User = #{updateUser},
where id = #{id}
