mapper.select:
select * from sky_take_out.shopping_cart

    user_id = #{userId}
    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and dish_flavor = #{dishFlavor}

select number from sky_take_out.shopping_cart

    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and dish_flavor = #{dishFlavor}

mapper.update:
update sky_take_out.shopping_cart

    number = #{number}

where
user_id = #{userId}



        (dish_id = #{dishId} AND dish_flavor = #{dishFlavor})


    OR

     setmeal_id = #{setmealId}
